mapper.update:
UPDATE
    events
SET
    user_email = #{event.email}
    , title = #{event.title}
    , short_title = #{event.short_title}
    , project_id = #{event.project_id}
    , memo = #{event.memo}
    , start = #{event.start}
    , end = #{event.end}
    , updated_at = CURRENT_TIMESTAMP
WHERE
    id = #{event.id}

