mapper.update:
UPDATE
    projects
SET
    name = #{name_new}
    , color_id = #{color_id}
    , memo = #{memo}
    , user_email = #{email}
    , updated_at = CURRENT_TIMESTAMP
WHERE
    name = #{name_old}
    AND
    user_email = #{email}

UPDATE
    projects
SET
    name = #{project.name}
    , color_id = #{project.color_id}
    , memo = #{project.memo}
    , user_email = #{project.email}
    , updated_at = CURRENT_TIMESTAMP
WHERE
    id = #{project.id}

